import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';

const ExerciseItem = ({
  exerciseItem: {
    _id,
    name,
    min,
    max,
    factor
  },
}) => {
  return (
    <Fragment>

      <div className="exercise-container-tables">
        {/* <table>
          <tr>
            <td style={ { color: '#fff', paddingRight: '1em', fontSize: 13 } }>{ name }</td>
            <td style={ { color: '#fff', paddingRight: '1em', fontSize: 13 } }>{ min }</td>
            <td style={ { color: '#fff', paddingRight: '1em', fontSize: 13 } }>{ max }</td>
          </tr>
        </table > */}
        <ul>
          <li> { name } { min } { max }</li>
        </ul>
      </div>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  exercise: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  exercise: state.exercise
});

export default connect(mapStateToProps)(ExerciseItem);
