import React, { useEffect, Fragment } from 'react';
import Spinner from '../layout/Spinner';
import Navbar from '../layout/Navbar';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { getClientProfile } from '../../actions/profile';
import AddNewRM from '../clients/AddNewRM';
import EditClientProfile from '../clients/EditClientProfile';
import { Link } from 'react-router-dom';

const Client = ({ match, getClientProfile, profile: { clientProfile, loading } }) => {



  useEffect(() => {
    getClientProfile(match.params.id);
    console.log(match.params.id);
  }, [getClientProfile, match.params.id]);

  return (
    <div>
      <Navbar />
      <Fragment>
        { clientProfile === null || loading ? <Spinner /> :
          (<div className="client-details-container">
            <h2>
              { clientProfile.clientFirstName }
            </h2>
            { clientProfile.clientOneRM.map((rm) =>
              <span>
                Bench Press One RM { rm.benchPress }
                <br />
               Squat One RM { rm.squat }
              </span>
            ) }</div>) }
        <Link to={ `/edit/${_id}` }>
          <i className="fas fa-user-edit" style={ { color: '#61c9a8', paddingRight: 8 } }></i>
          <EditClientProfile clientId={ clientProfile._id } />
              Edit Profile</Link>
        <Link to={ `/add-rm/${_id}` }>
          <i className="fas fa-weight-hanging" style={ { color: '#61c9a8', paddingRight: 8 } }></i>Add new RM</Link>
      </Fragment>

    </div>
  );
};

Client.propTypes = {
  getClientProfile: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  profile: state.profile,
});
export default connect(mapStateToProps, { getClientProfile })(Client);
