import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
import { getExercises } from '../../actions/exercise';
import ExerciseItem from './ExerciseItem';

const ShowAllExercises = ({ getExercises, exercises: { exercises, loading } }) => {
  useEffect(() => {
    getExercises();
  }, [getExercises]);
  return (
    <Fragment>
      { loading ? (
        <Spinner />
      ) : (

          <div className='exercises'>
            has
            { exercises.length > 0 ? (
              exercises.map((exercise) => (
                <ExerciseItem key={ exercise._id } exercise={ exercise } />
              ))
            ) : (
                <h4>No exercises found...</h4>
              ) }
          </div>


        ) }
    </Fragment>
  );
};

ShowAllExercises.propTypes = {
  getExercises: PropTypes.func.isRequired,
  exercises: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  exercises: state.exercise,
});

export default connect(mapStateToProps, { getExercises })(ShowAllExercises);
