import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { deleteExercise, getExerciseById } from '../../actions/exercise';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';


const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
  deleteExercise, getExerciseById
}) => {
  useEffect(() => {
    getExerciseById(_id);
  }, [getExerciseById]);

  const onClick = () => {
    deleteExercise(_id);
    window.history.back();
  };
  return (
    <Fragment>

      <tr>
        <td>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
        <td><Link to={ `/edit-exercise/${_id}` }>Edit</Link></td>
        <td><Link onClick={ () => onClick() }>Remove</Link></td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  deleteExercise: PropTypes.func.isRequired,
};
export default connect(null, { deleteExercise, getExerciseById })(ExerciseItem);
