import React, { Fragment } from 'react';
import { Link } from 'react-router-dom';
import { deleteExercise } from '../../actions/exercise';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';


const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
  deleteExercise, match
}) => {
  const onClick = () => {
    deleteExercise(match.params.id);
    window.history.back();
  };
  return (
    <Fragment>

      <tr>
        <td>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
        <td><Link to={ `/edit-exercise/${_id}` }>Edit</Link></td>
        <td>
          <Link onClick={ () => onClick() }>
            <i className="far fa-trash-alt" style={ { color: '#61c9a8', paddingRight: 8 } } ></i>Delete
                    </Link>
        </td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  deleteExercise: PropTypes.func.isRequired,
};
export default connect(null, { deleteExercise })(ExerciseItem);
