import React, { Fragment, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import EditExercise from './EditExercise';
import { getExerciseById, editExercise } from '../../actions/exercise';

const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
}) => {

  const onClick = () => {
    getExerciseById(_id);
  };
  return (
    <Fragment>

      <tr>
        <td onClick={ () => onClick() }>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  singleExercise: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  singleExercise: state.exercise
});

export default connect(mapStateToProps)(ExerciseItem);
