import React, { Fragment, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { deleteExercise } from '../../actions/exercise';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';


const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
  deleteExercise
}) => {
  const [state, setState] = useEffect(false);
  const onClick = () => {
    deleteExercise(_id);
    window.location.reload();
  };
  const handleModal = () => {
    setState(!state);
  };
  return (
    <Fragment>

      <tr>
        <td>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
        <td><Link to={ `/edit-exercise/${_id}` }>Edit</Link> { '' }
          <Link onClick={ () => onClick() }>Remove</Link>
          <button onClick={ handleModal }>modal</button>
        </td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  deleteExercise: PropTypes.func.isRequired,
};
export default connect(null, { deleteExercise })(ExerciseItem);
