import React, { Fragment, useEffect } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import Spinner from '../layout/Spinner';
import { getExercises } from '../../actions/exercise';
import ExerciseItem from './ExerciseItem';
import Navbar from '../layout/Navbar';



const ShowAllExercises = ({ getExercises, exercise: { exercises, loading } }) => {
  useEffect(() => {
    getExercises();
  }, [getExercises, loading]);
  return (
    <Fragment>
      <Navbar />
      { loading ? (
        <Spinner />
      ) :
        <section className='exercises'>

          <div className="dark-overlay">

            <div className="exercises-container">
              { exercises.length > 0 && (
                <div className="exercise-headers">

                  <h2 className="head" style={ { paddingLeft: '2em', display: 'flex', justifyContent: 'space-evenly', borderBottom: '1px solid', textAlign: 'center', position: 'fixed', maxWidth: '500px', marginBottom: '0.5em' } }>
                    <span style={ { paddingRight: '2em' } }>Exercise</span>
                    <span style={ { paddingRight: '2em' } }>Min</span>
                    <span style={ { paddingRight: '2em' } }>Max</span>
                    <span style={ { paddingRight: '2em' } }>Type</span>
                    <span style={ { paddingRight: '2em' } }>Actions</span>
                  </h2>

                  <table>
                    <tbody>
                      {
                        exercises.map((exerciseItem) => (
                          <ExerciseItem key={ exerciseItem._id } exerciseItem={ exerciseItem } />
                        ))
                      }
                    </tbody>
                  </table>
                </div>
              ) }</div>
          </div>
        </section>


      }
    </Fragment >
  );
};

ShowAllExercises.propTypes = {
  getExercises: PropTypes.func.isRequired,
  exercise: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  exercise: state.exercises,
});

export default connect(mapStateToProps, { getExercises })(ShowAllExercises);
