import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { deleteExercise } from '../../actions/exercise';

const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
}) => {
  const onClick = () => {
    deleteExercise(match.params.id);
    window.history.back();
  };
  return (
    <Fragment>

      <tr>
        <td>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
        <td><Link onClick={ () => onClick() }>
          <i className="far fa-trash-alt" style={ { color: '#61c9a8', paddingRight: 8 } } ></i>Remove
                    </Link></td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  singleExercise: PropTypes.object.isRequired,
};
const mapStateToProps = (state) => ({
  singleExercise: state.exercise
});

export default connect(mapStateToProps)(ExerciseItem);
