import React from 'react';
import { Link } from 'react-router-dom';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import DashboardActions from '../dashboard/DashboardActions';

const ClientItem = ({
  client: {
    _id,
    clientFirstName,
    clientLastName,
    clientPhone,
    clientEmail,
    clientSport,
  }
}) => {
  return (
    <div className='profile bg-light'>
      <div>
        <h2>{ clientFirstName } { !clientLastName ? '' : clientLastName }</h2>
        <div className="client inner">
          <span>Phone: { !clientPhone ? 'n/a' : clientPhone }</span>
          <br />
          <span>Email: { !clientEmail ? 'n/a' : clientEmail }</span>
          <br />
          <span>Sport: { !clientSport ? 'n/a' : clientSport }</span>
        </div>
        <Link to={ `/client/${_id}` } className='btn btn-primary'>
          View Client
      </Link>
        <DashboardActions />
      </div>
    </div>
  );
};

ClientItem.propTypes = {
  clientProfile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  clientProfile: state.profile,
});
export default connect(mapStateToProps)(ClientItem);
