import React, { Fragment } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import { deleteExercise } from '../../actions/exercise';

const ExerciseItem = ({
  exerciseItem: {
    _id,
    exercise,
    min,
    max,
    factor
  },
}) => {
  const onClick = () => {
    deleteExercise(_id);
    window.history.back();
  };
  return (
    <Fragment>

      <tr>
        <td>{ exercise }</td>
        <td>{ min }</td>
        <td>{ max }</td>
        <td><Link onClick={ () => onClick() }>Remove</Link></td>
      </tr>
    </Fragment>
  );
};

ExerciseItem.propTypes = {
  singleExercise: PropTypes.object.isRequired,
  deleteExercise: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  singleExercise: state.exercise
});

export default connect(mapStateToProps, { deleteExercise })(ExerciseItem);
