import React, { useState } from 'react';
import { Link } from 'react-router-dom';


export const Register = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    passwordRepeat: '',
  });

  const { firstName, lastName, email, password, passwordRepeat } = formData;

  const handleChange = (e) => setFormData({ ...formData, [e.target.name]: e.target.value });
  const handleSubmit = (e) => {
    e.preventDefault();
    if (password !== passwordRepeat) {
      console.log('passwords do not much');
    } else {
      console.log(formData);
    }
  };

  return (
    <div className="register">
      <div className="register-container">
        <div className="register-card">
          <div className="register-card-header">
            <h3>Register</h3>
          </div>
          <div className="card-body">
            <form onSubmit={ (e) => handleSubmit }>
              <div className="register-input-group">
                <span className="register-input-group-text">
                  <input type="text" required className="form-control" name="firstName" value={ firstName } onChange={ (e) => handleChange } placeholder=" First name" />
                </span>
                <span className="register-input-group-text">
                  <input type="text" className="form-control" name="lastName" value={ lastName } onChange={ (e) => handleChange } placeholder=" Last name (optional)" />
                </span>
                <span className="register-input-group-text">
                  <input type="email" required className="form-control" name="email" value={ email } onChange={ (e) => handleChange } placeholder=" Email address" />
                </span>
                <br />
                <span className="register-input-group-text">
                  <input type="password" required className="form-control" name="password" minLength="6" value={ password } onChange={ (e) => handleChange } placeholder=" Password" />
                </span>
                <span className="register-input-group-text">
                  <input type="password" required className="form-control" name="passwordRepeat" value={ passwordRepeat } onChange={ (e) => handleChange } placeholder=" Confirm password" />
                </span>

              </div>
              <div className="button">
                <Link to="/register" className="btn-login">Register</Link>
              </div>
            </form>
          </div>
          <div className="card-footer">
            <div className="d-flex justify-content-center links">
              Have an account?<Link to="/#">Login</Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Register;
