import React, { useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import DashboardActions from '../dashboard/DashboardActions';
import { connect } from 'react-redux';
import { getClientProfile } from '../../actions/profile';

const Client = ({ match, getClientProfile, clientProfile: { loading, profile } }) => {
  //use state for the RM form data
  // const [formData, setFormData] = useState({
  //   benchPress: '',
  //   squat: '',
  // });

  // const { benchPress, squat } = formData;
  // use effect to get the profile
  useEffect(() => {
    getClientProfile(match.params.id);
  }, [getClientProfile]);
  console.log(profile);

  // from the profile show name and one rm



  // use dashboard actions to edit and  add

  return (
    <div>
      <p>Client is</p>
      <DashboardActions />
    </div>
  );
};

Client.propTypes = {
  clientProfile: PropTypes.object.isRequired,
  getClientProfile: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  clientProfile: state.profile
});
export default connect(mapStateToProps, { getClientProfile })(Client);
