import React, { Fragment, useEffect, useState } from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { Link } from 'react-router-dom';
import Spinner from '../layout/Spinner';
import { getAllProfiles } from '../../actions/profile';
import ClientItem from './ClientItem';
import Pagination from '../layout/Pagination';
import { get } from 'mongoose';

const ShowAllClients = ({ getAllProfiles, profile: { clientProfiles, loading } }) => {
  const [person, setPerson] = useState([]);
  const [currentTab, setCurrentTab] = useState(1);
  const [personPerTab] = useState(5);

  useEffect(() => {
    getAllProfiles();
    setPerson(getAllProfiles());

  }, [getAllProfiles, loading]);

  const indexOfLastPerson = currentTab * personPerTab;
  const indexOfFirstPerson = indexOfLastPerson - personPerTab;
  const current = person.slice(indexOfFirstPerson, indexOfLastPerson);



  const paginate = (pageNumber) => {
    setCurrentTab(pageNumber);
  };

  return (
    <Fragment>
      { loading ? (
        <Spinner />
      ) :
        <div className='clients'>
          { clientProfiles.length > 0 ? (
            <div className="client-headers">
              <table className="client-table">
                <thead>
                  <tr>
                    <th style={ { color: '#fff', padding: '1em' } }>Client Name</th>
                    <th style={ { color: '#fff', padding: '1em' } }>Information</th>
                    <th style={ { color: '#fff', padding: '1em' } }>View</th>
                  </tr>
                </thead>
                <tbody>{
                  clientProfiles.map((client) => (
                    <ClientItem key={ client._id } client={ client } paginate={ paginate } />
                  ))
                }
                </tbody>
              </table>
              <Pagination personPerTab={ personPerTab } numOfPeople={ clientProfiles.length } />
            </div>) : (
              <p style={ { color: '#fff', fontSize: 17 } }>There are currently no clients for this account. Click <Link to='/add'>here </Link> to add clients.</p>
            ) }
        </div>
      }
    </Fragment>
  );
};

ShowAllClients.propTypes = {
  getAllProfiles: PropTypes.func.isRequired,
  profile: PropTypes.object.isRequired,
};

const mapStateToProps = (state) => ({
  profile: state.profile,
});

export default connect(mapStateToProps, { getAllProfiles })(ShowAllClients);
